import React, { useState, useEffect } from "react";


const mydata = ()=>{
  const fanialData = localStorage.getItem("userData");

  if(fanialData){
    return JSON.parse(fanialData)
  }else{
    return []
  }
}

const Crud = () => {

  const [data, setData] = useState({
    username: "",
    password: "",
    email: "",
    phonenumber: "",
  });

  const [local, setLocal] = useState(mydata());

  const changeHandler = (event) => {
    setData({ ...data, [event.target.name]: [event.target.value] });
  };

  const submitHandler = (event) => {
   
    event.preventDefault();
    setLocal([...local,data])

  };
  

  useEffect(()=>{

    localStorage.setItem('userData',JSON.stringify(local))
    
   
  },[local])

  
  const deleteHandleer =(index)=>{
    alert("are yousure want to delete")
    const filterData = local.filter((element)=>{
      return local.indexOf(element) !== index
    })

    console.log(filterData)
    setLocal(filterData)
  }

  const editHandker = (user)=>{
    console.log()
    let mylocal = local
    setData(
      {
        key:index,
        username: mylocal.username,
        password: mylocal.password,
        email: mylocal.email,
        phonenumber: mylocal.phonenumber,

      }
    )
  }

  

  return (
    <>
      <form
        style={{
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
          width: "100%",
        }}
        onSubmit={submitHandler}
      >
        <label htmlFor="username">Enter Username</label>
        <input
          type="text"
          id="username"
          name="username"
          onChange={changeHandler}
        ></input>
        <label htmlFor="password">Enter Password</label>
        <input
          type="password"
          id="password"
          name="password"
          onChange={changeHandler}
        ></input>
        <label htmlFor="email">Enter Email</label>
        <input
          type="email"
          id="email"
          name="email"
          onChange={changeHandler}
        ></input>
        <label htmlFor="phonenumber">Enter Phonenumber</label>
        <input
          type="number"
          name="phonenumber"
          id="phonenumber"
          onChange={changeHandler}
        ></input>
        <button>Submit</button>
      </form>
{local.length <= 0 ? <h1>Enter User Details</h1>:
      <table>
        <thead>
          <tr>
            <th>username</th>
            <th>password</th>
            <th>email</th>
            <th>phonenumber</th>
            <th colSpan={2}>actons</th>
          </tr>
        </thead>
        <tbody>
        {
          local.map((user,index)=>(
            <tr key={index}>
              <td>{user.username}</td>
              <td>{user.password}</td>
              <td>{user.email}</td>
              <td>{user.phonenumber}</td>
              <td onClick={()=>editHandker(user)}>Edit</td>
              <td  onClick={()=>deleteHandleer(index)}>Delete</td>
            </tr>
          ))
        }

        </tbody>
 
      </table>}
    </>
  );
};

export default Crud;
