import React, { useState, useEffect } from "react";


const mydata = ()=>{
  const fanialData = localStorage.getItem("userData");
  if(fanialData){
    return JSON.parse(fanialData)
  }else{
    return []
  }
}

const Crud = () => {
  const [data, setData] = useState({
    username: "",
    password: "",
    email: "",
    phonenumber: "",
  });

  const [local, setLocal] = useState(mydata());
  console.log(local,"local")
 
  const changeHandler = (event) => {
    setData({ ...data, [event.target.name]: [event.target.value] });
  };

  const submitHandler = (event) => {
   
    event.preventDefault();
    // console.log(data)
    // let NewData = JSON.parse(localStorage.getItem("userData"))
    // NewData.push(data)
    // localStorage.setItem('userData',JSON.stringify(NewData))
    setLocal([...local,data])
  };
  

  useEffect(()=>{

    localStorage.setItem('userData',JSON.stringify(local))
   
  },[local])

  

  return (
    <>
      <form
        style={{
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
          width: "100%",
        }}
        onSubmit={submitHandler}
      >
        <label htmlFor="username">Enter Username</label>
        <input
          type="text"
          id="username"
          name="username"
          onChange={changeHandler}
        ></input>
        <label htmlFor="password">Enter Password</label>
        <input
          type="password"
          id="password"
          name="password"
          onChange={changeHandler}
        ></input>
        <label htmlFor="email">Enter Email</label>
        <input
          type="email"
          id="email"
          name="email"
          onChange={changeHandler}
        ></input>
        <label htmlFor="phonenumber">Enter Phonenumber</label>
        <input
          type="number"
          name="phonenumber"
          id="phonenumber"
          onChange={changeHandler}
        ></input>
        <button>Submit</button>
      </form>

      <table>
        <tr>
          <th>username</th>
          <th>password</th>
          <th>email</th>
          <th>phonenumber</th>
        </tr>
 
      </table>
    </>
  );
};

export default Crud;
